// Signup.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Signup = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Exemple de validation simple
    if (!username || !email || !password) {
      alert("Veuillez remplir tous les champs");
      return;
    }

    try {
      // Ici, vous pouvez envoyer les données à votre serveur avec axios ou fetch
      // Exemple avec axios (à adapter selon votre backend) :
      // await axios.post("https://votre-api.com/signup", { username, email, password });

      // Pour l'exemple, nous stockons simplement les données dans le localStorage
      const userData = { username, email, password };
      localStorage.setItem("userData", JSON.stringify(userData));
      alert("Inscription réussie !");

      // Redirigez l'utilisateur vers la page d'accueil ou la page de connexion
      navigate("/");
    } catch (error) {
      console.error("Erreur lors de l'inscription", error);
      alert("Erreur lors de l'inscription");
    }
  };

  return (
    <div className="auth-page">
      <h2>Inscription</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nom d'utilisateur :</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Votre nom d'utilisateur"
          />
        </div>
        <div>
          <label>Email :</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Votre email"
          />
        </div>
        <div>
          <label>Mot de passe :</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Votre mot de passe"
          />
        </div>
        <button type="submit">S'inscrire</button>
      </form>
    </div>
  );
};

export default Signup;
